import React, { Component } from 'react';
import Header from '../template/Header';
import Mainmessage from '../template/Mainmessage';
import Sarchbar from '../template/Sarchbar';

class App extends Component {
  //コンストラクタ stateを使う場会必須
  constructor (props) {
    super(props)
    this.state = {
      area: '',
      pref:''
    };
//textfieldに入力があった場合stateに値をセット
    let handleChange_area = (event) => {
      this.setState({
        area: event.target.area,
      });
    };

    let handleChange_pref = (event) => {
      this.setState({
        pref: event.target.pref
      });
    };
  }
  render() {
    return (
      <div className='main'>
        <Header />
          <Mainmessage />
            <Sarchbar />
      </div>
    );
  }
}

export default App;
