import React, { Component } from 'react';
import { createGlobalStyle } from "styled-components";
import axios from 'axios';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { styled } from '@material-ui/styles';
import color from '@material-ui/core/colors/teal';



const GlobalStyle = createGlobalStyle`
@import url('https://fonts.googleapis.com/css?family=Kosugi+Maru&display=swap');
@import url('../../../weather-icons-master/css/weather-icons.min.css');
div .root{
  text-align:center;
  width: 100%;
  font-family: 'Kosugi Maru', sans-serif;
}
span .font{
  font-family: 'Kosugi Maru', sans-serif;
  font-size: 100px;
}
`;
//CSS定義
const MyTable = styled(Table)({
  height: "490px",
  padding: '100px 30px',
  width:"600px",
  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
  marginLeft  : 'auto',
    marginRight : 'auto',
});
const MyPaper = styled(Paper)({
  height: "500pxpx",
  padding: '30px 30px',
  width:"700px",
  textAlign: 'center',
  marginLeft  : 'auto',
    marginRight : 'auto',
});
class Ski_area_table extends Component {
    //コンストラクタ stateを使う場会必須
    constructor (props) {
      super(props)
      this.state = {
        json :[],
      };
    
    var url = `http://localhost:4000/wether`;
    axios.post(url, {
      latlong: this.props.data.latlong
    })
    .then(response => (this.JsonStateSet(response)))
    .catch(function (error) {
      console.log(error);
    });

    }
    JsonStateSet(response){
      this.setState({json:response.data.daily})
        }

  render() {

    var rowsDay = [];
    var rowswether = [];
    var rowstempertureHigh = [];
    var rowstempertureLow = [];
    var rowsprecipProbbility = [];
    var rowsprecipIntensity = [];
    var rowswindSpeed = [];

    if(this.state.json.data !== undefined){
      var todaydate = new Date();
      for(var i = 0;i < 3; i++){
        rowsDay.push(<TableCell align="center"><span className="font">
        {`${todaydate.getMonth()+1}/${todaydate.getDate()+i}`}
        </span>
        </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowswether.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].summary}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowstempertureHigh.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].temperatureHigh}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowstempertureLow.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].temperatureLow}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowsprecipProbbility.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].precipProbability}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowsprecipIntensity.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].precipIntensity}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowswindSpeed.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].windSpeed}
          </span>
          </TableCell>);
      }
  }
    return (
    <div className="root">
      <GlobalStyle />
     <MyPaper >
     <span className="font">ABC</span>
      <div className="table">
        <MyTable>
          <TableHead>
            <TableRow>
            <TableCell colSpan={3} align="center"><span className="font">{this.props.data.name}</span></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
              <TableRow >
                {rowsDay}
              </TableRow>
              <TableRow >
                {rowswether}
              </TableRow>
              <TableRow >
                {rowstempertureHigh}
              </TableRow>
              <TableRow >
                {rowstempertureLow}
              </TableRow>
              <TableRow >
                {rowsprecipProbbility}
              </TableRow>
              <TableRow >
                {rowsprecipIntensity}
              </TableRow>
              <TableRow >
                {rowswindSpeed}
              </TableRow>
          </TableBody>
        </MyTable>
      </div>
    </MyPaper>
  
    </div>
    )}
          
}

export default Ski_area_table;
