import React, { Component } from 'react';
import { createGlobalStyle } from "styled-components";
import axios from 'axios';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';



const GlobalStyle = createGlobalStyle`
div .root{
  text-align:center;
  width: 100%;
}
div .table{
  text-align:center;
  width: 50%;
  background-color:brack;
}
`;
//CSS定義
const styles = {
  table: { width: 400, height: 100}   //テーブルの大きさ指定
};
class Ski_area_table extends Component {
    //コンストラクタ stateを使う場会必須
    constructor (props) {
      super(props)
      this.state = {
        json :[],
      };
    
    var url = `http://localhost:4000/wether`;
    axios.post(url, {
      latlong: this.props.data.latlong
    })
    .then(response => (this.JsonStateSet(response)))
    .catch(function (error) {
      console.log(error);
    });

    }
    JsonStateSet(response){
      this.setState({json:response.data.daily})
        }

  render() {

    var rows = [];
    if(this.state.json.data !== undefined){
      for(var i = 0;i < 3; i++){
          rows.push(<TableCell align="right">
          {this.state.json.data[i].icon}
          </TableCell>);
      }
  }
    return (
    <div className="root">
      <GlobalStyle />
      {this.props.data.name}
      {this.props.data.pref}
      {this.props.data.address}
      {this.props.data.latlong}
      {console.log(this.state.json)}
      {console.log("aaa")}
      <Paper >
      <div className="table">
        <Table 
        style={styles.table}
        >
          <TableHead>
            <TableRow>
              <TableCell align="center">{this.props.data.name}</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
              <TableRow >
                {rows}
              </TableRow>
          </TableBody>
        </Table>
      </div>
    </Paper>
  
    </div>
    )}
          
}

export default Ski_area_table;
