import React, { Component } from 'react';
import { createGlobalStyle } from "styled-components";
import axios from 'axios';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { styled } from '@material-ui/styles';



const GlobalStyle = createGlobalStyle`
@import url('https://fonts.googleapis.com/css?family=Kosugi+Maru&display=swap');
@import url('https://fonts.googleapis.com/css?family=M+PLUS+1p&display=swap');
div .root{
  text-align:center;
  width: 100%;
}
div .font{
  font-family: 'Kosugi Maru', sans-serif;
}
div .bigfont{
  font-family: 'M PLUS 1p', sans-serif;
  font-size: 18px;
  color: white;
}
`;
//CSS定義
const MyTable = styled(Table)({
  height: "490px",
  padding: '100px 30px',
  width:"600px",
  marginLeft  : 'auto',
  marginRight : 'auto',
});
const Headercell = styled(TableCell)({
  background: 'linear-gradient(45deg, #0a0a0a 30%, #FF8E53 90%)',
  size:"small",
});
const MyPaper = styled(Paper)({
  height: "500px",
  padding: '30px 30px',
  width:"640px",
  textAlign: 'center',
  marginLeft  : 'auto',
    marginRight : 'auto',
});
class Ski_area_table extends Component {
    //コンストラクタ stateを使う場会必須
    constructor (props) {
      super(props)
      this.state = {
        json :[],
      };
    
    var url = `http://localhost:4000/wether`;
    axios.post(url, {
      latlong: this.props.data.latlong
    })
    .then(response => (this.JsonStateSet(response)))
    .catch(function (error) {
      console.log(error);
    });

    }
    JsonStateSet(response){
      this.setState({json:response.data.daily})
        }

  render() {

    var rowsDay = [];
    var rowswether = [];
    var rowstempertureHigh = [];
    var rowstempertureLow = [];
    var rowsprecipProbbility = [];
    var rowsprecipIntensity = [];
    var rowswindSpeed = [];

    if(this.state.json.data !== undefined){
      var todaydate = new Date();
      for(var i = 0;i < 3; i++){
        rowsDay.push(<TableCell align="center"><span className="font">
        {`${todaydate.getMonth()+1}/${todaydate.getDate()+i}`}
        </span>
        </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowswether.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].summary}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowstempertureHigh.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].temperatureHigh}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowstempertureLow.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].temperatureLow}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowsprecipProbbility.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].precipProbability}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowsprecipIntensity.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].precipIntensity}
          </span>
          </TableCell>);
      }
      for(i = 0;i < 3; i++){
        rowswindSpeed.push(<TableCell align="center"><span className="font">
          {this.state.json.data[i].windSpeed}
          </span>
          </TableCell>);
      }
  }
    return (
    <div className="root">
      <GlobalStyle />
      <i class="wi wi-rain"></i>
<i class="wi wi-day-sunny"></i>
<i class="wi wi-day-cloudy"></i>
<i class="wi wi-stars"></i>
<i class="wi wi-cloud"></i>
<i class="wi wi-na"></i>
<i class="wi wi-umbrella"></i>
<i class="wi wi-lightning"></i>
<i class="wi wi-tsunami"></i>
<i class="wi wi-wind-direction"></i>
<i class="wi wi-snowflake-cold"></i>
     <MyPaper >
      <div className="table">
        <MyTable>
            <TableRow>
            <Headercell colSpan={3} align="left">
              <span className="bigfont">
                {this.props.data.name}
              </span>
              </Headercell>
            </TableRow>
          <TableBody>
              <TableRow >
                {rowsDay}
              </TableRow>
              <TableRow >
                {rowswether}
              </TableRow>
              <TableRow >
                {rowstempertureHigh}
              </TableRow>
              <TableRow >
                {rowstempertureLow}
              </TableRow>
              <TableRow >
                {rowsprecipProbbility}
              </TableRow>
              <TableRow >
                {rowsprecipIntensity}
              </TableRow>
              <TableRow >
                {rowswindSpeed}
              </TableRow>
          </TableBody>
        </MyTable>
      </div>
    </MyPaper>
  
    </div>
    )}
          
}

export default Ski_area_table;
